import { useState } from "react";

function Transaction({updatePendingAmount,setflag,customer_id}){

    const [description,setDesc]=useState('');
    const [amount,setAmount]=useState('');
    async function handleSubmit(e){
        e.preventDefault();
        const response = await fetch(`http://localhost:4000/customer/${customerId}/add-transaction`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              amount: parseFloat(amount), // Convert to float
              description,
              date,
            }),
            credentials: 'include',
          });
      
          if (response.ok) {
            // Update the pending amount in the parent component
            updatePendingAmount(customerId, parseFloat(amount));
            closeModal(); // Close the modal on success
          }

    }
    return(
        <div className="new-customer-container">
          <form className="new-customer-form" onSubmit={handleSubmit}>
            <h2>Add New Transaction</h2>
            <div className="form-group">
              <label htmlFor="name">Description</label>
              <input
                type="text"
                id="description"
                placeholder="Enter Description"
                value={description}
                onChange={(e) => setDesc(e.target.value)}
                required
              />
            </div>
           
            <div className="form-group">
              <label htmlFor="pendingAmount">Pending Amount</label>
              <input
                type="number"
                id="amount"
                placeholder="Enter amount"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                required
              />
            </div>
            
            <button type="submit" className="submit-button">Add Transaction</button>
          </form>
        </div>
      );
}
export default Transaction;
