import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';

function NewCustomer() {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [pendingAmount, setPendingAmount] = useState('');
  const [photo, setPhoto] = useState(null);
  const [redirec,setredirec]=useState(false);
  async function handleSubmit(e) {
    e.preventDefault();
    // Upload image
    const formData = new FormData();
    formData.set('photo', photo);
    formData.set('name', name);
    formData.set('phone', phone);
    formData.set('pendingAmount', pendingAmount);

    const response = await fetch('http://localhost:4000/add', {
      method: 'POST',
      body: formData,
      credentials:'include',
    });
    con
    console.log(await response.json());

    if(response.ok){
      setredirec(true);
    }

    // Clear form or show success message
    if(redirec){
      return <Navigate to={'/'} />;
    }
  }

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value) } required/>
      <input type="text" placeholder="Phone" value={phone} onChange={(e) => setPhone(e.target.value)} required/>
      <input type="number" placeholder="Pending Amount" value={pendingAmount} onChange={(e) => setPendingAmount(e.target.value)} required/>
      <input type="file" onChange={(e) => setPhoto(e.target.files[0])} required/>
      <button type="submit">Add Customer</button>
    </form>
  );
}


export default NewCustomer;
