import { useState } from "react";
import { Navigate } from "react-router-dom";

function Register(){
    const [username,setuser]=useState("");
    const [password,setpass]=useState("");
    const [redirect,setred]=useState(false);
    async function Handlesubmit(evnt) {
        evnt.preventDefault();
        const response = await fetch('http://localhost:4000/register', {
          method: 'POST',
          body: JSON.stringify({username,password}),
          headers: {'Content-Type':'application/json'},
        });
        if(response.ok){
            setred(true);
        }else{
            alert('Wrong ')
        }
        if(redirect){
            <Navigate to={'/'} />
        }
      }
    return <form className="register" onSubmit={Handlesubmit}>
    <h1>Register</h1>
    <input type="text" placeholder="username" name="username" value={username} onChange={evnt => setuser(evnt.target.value) } required></input>
    <input type="password" placeholder="password" name="password" value={password} onChange={evnt => setpass(evnt.target.value)} required></input>
    <button>Register</button>
</form>;
}

export default Register;
